// Componente contador

import { useState } from 'react';
import './Counter.css';

function Counter() {
    const [counter, setCounter] = useState(0);

    const incrementar = () => {
        setCounter(counter++);
    }

    return(
        <div className='counter'>
            <p>
                Hiciste click {counter} veces en este bot√≥n
            </p>
            <button onClick={incrementar}>Incrementar</button>
        </div>
    );
}

export default Counter;
