    const [targetSeconds, setTargetSeconds] = useState()

    return (
        <div className='count-down'>
            <p>
                ¿Cuántos segundos quieres contar?
            </p>
            <form action="#">
                <input type="number" />
                <button>Iniciar</button>
            </form>
        </div>
    );
}

export default CountDown;
